import React, { useState } from 'react';
import './App.css';
import Orderbook from './Orderbook';
import OrderForm from './OrderForm';

const App: React.FC = () => {
  const [selectedAsset, setSelectedAsset] = useState<string>('BTC');
  const [orderbookUpdate, setOrderbookUpdate] = useState<boolean>(false);

  const handleAssetChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedAsset(event.target.value);
  };

  const handleOrderSubmit = (order: any) => {
    setOrderbookUpdate(!orderbookUpdate); // Toggle state to trigger orderbook update
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptocurrency Order Book</h1>
        <select value={selectedAsset} onChange={handleAssetChange}>
          <option value="BTC">BTC</option>
          <option value="ETH">ETH</option>
        </select>
      </header>
      <div className="orderbook-container">
        <Orderbook asset={selectedAsset} update={orderbookUpdate} />
        <OrderForm asset={selectedAsset} onOrderSubmit={handleOrderSubmit} />
      </div>
    </div>
  );
};

export default App;
