import React, { useState } from 'react';
import './OrderForm.css';

interface OrderFormProps {
  asset: string;
  addOrder: (side: 'BUY' | 'SELL', price: number, quantity: number) => void;
}

const OrderForm: React.FC<OrderFormProps> = ({ asset, addOrder }) => {
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [message, setMessage] = useState<string>('');

  const handleOrderSubmit = (side: 'BUY' | 'SELL') => {
    const parsedQuantity = typeof quantity === 'string' ? parseFloat(quantity) : quantity;
    const parsedPrice = typeof price === 'string' ? parseFloat(price) : price;

    if (parsedQuantity <= 0 || parsedPrice <= 0) {
      setMessage('Quantity and price must be greater than zero.');
      return;
    }

    addOrder(side, parsedPrice, parsedQuantity);
    setMessage(`Order ${side} successfully placed.`);
    setQuantity('');
    setPrice('');
  };

  return (
    <div className="OrderForm">
      <h2>Place Order</h2>
      <div>
        <label>Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
      </div>
      <div>
        <label>Price: </label>
        <input
          type="number"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
        />
      </div>
      <div>
        <button onClick={() => handleOrderSubmit('BUY')}>Place Buy Order</button>
        <button onClick={() => handleOrderSubmit('SELL')}>Place Sell Order</button>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderForm;
