import React, { useState, useEffect } from 'react';
import './App.css';
import Orderbook from './Orderbook';
import OrderForm from './OrderForm';
import { getOrderbook, sendTrade } from './api/api';

interface Order {
  price: string;
  quantity: string;
}

interface OrderbookData {
  bids: Order[];
  asks: Order[];
}

const App: React.FC = () => {
  const [selectedAsset, setSelectedAsset] = useState<string>('BTC');
  const [orderbook, setOrderbook] = useState<OrderbookData>({ bids: [], asks: [] });

  const handleAssetChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedAsset(event.target.value);
  };

  useEffect(() => {
    getOrderbook(selectedAsset)
      .then(data => {
        const formattedData = {
          bids: formatOrders(data.bids).reverse(),
          asks: formatOrders(data.asks).reverse(),
        };
        setOrderbook(formattedData);
      })
      .catch(error => console.error('Error fetching orderbook:', error));
  }, [selectedAsset]);

  const formatOrders = (orders: [string, string][]) => {
    return orders.map(([price, quantity]) => ({
      price: parseFloat(price).toString(),
      quantity: parseFloat(quantity).toString(),
    })).sort((a, b) => parseFloat(a.price) - parseFloat(b.price));
  };

  const addOrder = (order: Order, side: 'bids' | 'asks') => {
    setOrderbook(prevOrderbook => ({
      ...prevOrderbook,
      [side]: [...prevOrderbook[side], order].sort((a, b) => parseFloat(a.price) - parseFloat(b.price))
    }));
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptocurrency Order Book</h1>
        <select value={selectedAsset} onChange={handleAssetChange}>
          <option value="BTC">BTC</option>
          <option value="ETH">ETH</option>
        </select>
      </header>
      <div className="App-body">
        <Orderbook orderbook={orderbook} />
        <OrderForm addOrder={addOrder} />
      </div>
    </div>
  );
};

export default App;
