import React, { useState } from 'react';
import './App.css';
import Orderbook from './Orderbook';
import OrderForm from './OrderForm';

interface Order {
  price: number;
  quantity: number;
}

interface OrderbookData {
  bids: Order[];
  asks: Order[];
}

const App: React.FC = () => {
  const [selectedAsset, setSelectedAsset] = useState<string>('BTC');
  const [orderbook, setOrderbook] = useState<OrderbookData>({ bids: [], asks: [] });

  const handleAssetChange = (event: React.ChangeEvent<HTMLSelectElement>) => {
    setSelectedAsset(event.target.value);
  };

  const addOrder = (side: 'BUY' | 'SELL', price: number, quantity: number) => {
    setOrderbook(prevOrderbook => {
      const updatedOrderbook = { ...prevOrderbook };
      if (side === 'BUY') {
        updatedOrderbook.bids = [...updatedOrderbook.bids, { price, quantity }].sort((a, b) => b.price - a.price);
      } else {
        updatedOrderbook.asks = [...updatedOrderbook.asks, { price, quantity }].sort((a, b) => a.price - b.price);
      }
      return updatedOrderbook;
    });
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Cryptocurrency Order Book</h1>
        <select value={selectedAsset} onChange={handleAssetChange}>
          <option value="BTC">BTC</option>
          <option value="ETH">ETH</option>
        </select>
      </header>
      <div className="App-container">
        <Orderbook asset={selectedAsset} orderbook={orderbook} />
        <OrderForm asset={selectedAsset} addOrder={addOrder} />
      </div>
    </div>
  );
};

export default App;
