import React, { useState } from 'react';
import { Button, TextField, Typography, Paper } from '@mui/material';
import './OrderForm.css';
import { sendTrade } from './api/api';

interface OrderFormProps {
  asset: string;
  onOrderPlaced: () => void;
}

const OrderForm: React.FC<OrderFormProps> = ({ asset, onOrderPlaced }) => {
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [notional, setNotional] = useState<number | string>('');
  const [orderType, setOrderType] = useState<'LIMIT' | 'MARKET'>('LIMIT');
  const [message, setMessage] = useState<string>('');

  const handleOrderSubmit = (side: 'BUY' | 'SELL') => {
    const order = {
      asset,
      side,
      type: orderType,
      quantity: typeof quantity === 'string' ? parseFloat(quantity) : quantity,
      price: orderType === 'LIMIT' ? (typeof price === 'string' ? parseFloat(price) : price) : undefined,
      notional: typeof notional === 'string' ? parseFloat(notional) : notional,
    };

    sendTrade(order)
      .then(data => {
        if (data.error) {
          setMessage(data.error);
        } else {
          setMessage(`Order ${side} successfully placed with id: ${data.id}`);
          onOrderPlaced(); // Notify parent component to refresh orderbook
        }
      })
      .catch(error => setMessage('Order submission failed'));
  };

  return (
    <Paper className="OrderForm">
      <Typography variant="h6">Place Order</Typography>
      <div>
        <TextField
          label="Quantity"
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
          fullWidth
        />
      </div>
      {orderType === 'LIMIT' && (
        <div>
          <TextField
            label="Price"
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            fullWidth
          />
        </div>
      )}
      <div>
        <TextField
          label="Notional"
          type="number"
          value={notional}
          onChange={(e) => setNotional(e.target.value)}
          fullWidth
        />
      </div>
      <div>
        <Button variant="contained" color="primary" onClick={() => handleOrderSubmit('BUY')}>
          Place Buy Order
        </Button>
        <Button variant="contained" color="secondary" onClick={() => handleOrderSubmit('SELL')}>
          Place Sell Order
        </Button>
      </div>
      {message && <Typography variant="body2" color="error">{message}</Typography>}
    </Paper>
  );
};

export default OrderForm;
