import React, { useState, useEffect } from 'react';
import { getOrderbook } from './api';

const Orderbook: React.FC<{ asset: string }> = ({ asset }) => {
  const [orderbook, setOrderbook] = useState<{ bids: any[], asks: any[] }>({ bids: [], asks: [] });

  useEffect(() => {
    const fetchOrderbook = async () => {
      const data = await getOrderbook(asset);
      if (data) {
        setOrderbook(data);
      }
    };

    fetchOrderbook();
  }, [asset]);

  return (
    <div className="orderbook">
      <div className="orderbook-section">
        <h3>Asks</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount (USD)</span>
        </div>
        <div className="order-list asks-list">
          {orderbook.asks.map((ask, index) => (
            <div key={index} className="order-item">
              <span>{ask.price}</span>
              <span>{ask.quantity}</span>
              <span>{(ask.price * ask.quantity).toFixed(2)}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="orderbook-section">
        <h3>Bids</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount (USD)</span>
        </div>
        <div className="order-list bids-list">
          {orderbook.bids.map((bid, index) => (
            <div key={index} className="order-item">
              <span>{bid.price}</span>
              <span>{bid.quantity}</span>
              <span>{(bid.price * bid.quantity).toFixed(2)}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orderbook;
