import React, { useState } from 'react';
import OrderForm from './OrderForm';
import Orderbook from './Orderbook';
import './App.css'; // Assuming you have some global styles

const App: React.FC = () => {
  const [orders, setOrders] = useState<{ bids: any[]; asks: any[] }>({ bids: [], asks: [] });

  const addOrder = (newOrder: any) => {
    setOrders((prevOrders) => {
      const updatedOrders = { ...prevOrders };
      if (newOrder.side === 'BUY') {
        updatedOrders.bids.push(newOrder);
        updatedOrders.bids.sort((a, b) => b.price - a.price); // Sort bids descending
      } else {
        updatedOrders.asks.push(newOrder);
        updatedOrders.asks.sort((a, b) => a.price - b.price); // Sort asks ascending
      }
      return updatedOrders;
    });
  };

  return (
    <div className="app">
      <OrderForm asset="BTC" addOrder={addOrder} />
      <Orderbook asset="BTC" orders={orders} />
    </div>
  );
};

export default App;
