import React from 'react';
import './Orderbook.css'; // Import the Orderbook styles

interface Order {
  price: string;
  quantity: string;
}

interface OrderbookProps {
  asset: string;
  orders: { bids: Order[]; asks: Order[] };
}

const Orderbook: React.FC<OrderbookProps> = ({ asset, orders }) => {
  const calculateAmountInUSD = (price: string, quantity: string): string => {
    const amount = parseFloat(price) * parseFloat(quantity);
    return amount.toFixed(2);
  };

  return (
    <div className="orderbook">
      <div className="orderbook-section">
        <h3>Asks</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount in USD</span>
        </div>
        <div className="order-list asks-list">
          {orders.asks.map((ask, index) => (
            <div key={index} className="order-item">
              <span>{ask.price}</span>
              <span>{ask.quantity}</span>
              <span>{calculateAmountInUSD(ask.price, ask.quantity)}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="orderbook-section">
        <h3>Bids</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount in USD</span>
        </div>
        <div className="order-list bids-list">
          {orders.bids.map((bid, index) => (
            <div key={index} className="order-item">
              <span>{bid.price}</span>
              <span>{bid.quantity}</span>
              <span>{calculateAmountInUSD(bid.price, bid.quantity)}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orderbook;
