import React, { useState, useEffect, useRef } from 'react';
import './Orderbook.css';

interface Order {
  price: string;
  quantity: string;
}

interface OrderbookData {
  bids: Order[];
  asks: Order[];
}

interface OrderbookProps {
  asset: string;
}

const Orderbook: React.FC<OrderbookProps> = ({ asset }) => {
  const [orderbook, setOrderbook] = useState<OrderbookData>({ bids: [], asks: [] });
  const asksRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    fetch(`/orderbook/${asset}`)
      .then(response => response.json())
      .then(data => {
        const formattedData = {
          bids: data.bids
            .map((bid: [string, string]) => ({ price: bid[0], quantity: bid[1] }))
            .sort((a: Order, b: Order) => parseFloat(b.price) - parseFloat(a.price)),
          asks: data.asks
            .map((ask: [string, string]) => ({ price: ask[0], quantity: ask[1] }))
            .sort((a: Order, b: Order) => parseFloat(b.price) - parseFloat(a.price)),
        };
        setOrderbook(formattedData);
      })
      .catch(error => console.error('Error fetching orderbook:', error));
  }, [asset]);

  useEffect(() => {
    if (asksRef.current) {
      asksRef.current.scrollTop = asksRef.current.scrollHeight;
    }
  }, [orderbook.asks]);

  const calculateAmountInUSD = (price: string, quantity: string): string => {
    const amount = parseFloat(price) * parseFloat(quantity);
    return amount.toFixed(2);
  };

  return (
    <div className="orderbook">
      <div className="orderbook-section">
        <h3>Asks</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount in USD</span>
        </div>
        <div className="order-list asks-list" ref={asksRef}>
          {orderbook.asks.map((ask, index) => (
            <div key={index} className="order-item">
              <span>{ask.price}</span> <span>{ask.quantity}</span> <span>{calculateAmountInUSD(ask.price, ask.quantity)}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="orderbook-section">
        <h3>Bids</h3>
        <div className="order-header">
          <span>Price</span>
          <span>Quantity</span>
          <span>Amount in USD</span>
        </div>
        <div className="order-list bids-list">
          {orderbook.bids.map((bid, index) => (
            <div key={index} className="order-item">
              <span>{bid.price}</span> <span>{bid.quantity}</span> <span>{calculateAmountInUSD(bid.price, bid.quantity)}</span>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Orderbook;
