import React, { useState } from 'react';
import './OrderForm.css'; // Import the OrderForm styles

interface OrderFormProps {
  asset: string;
}

const OrderForm: React.FC<OrderFormProps> = ({ asset }) => {
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [notional, setNotional] = useState<number | string>('');
  const [orderType, setOrderType] = useState<'LIMIT' | 'MARKET'>('LIMIT');
  const [message, setMessage] = useState<string>('');

  // Update the notional when quantity or price changes
  useEffect(() => {
    if (quantity && price) {
      setNotional((parseFloat(quantity as string) * parseFloat(price as string)).toFixed(2));
    } else {
      setNotional('');
    }
  }, [quantity, price]);

  // Update the quantity when price and notional are provided
  useEffect(() => {
    if (price && notional) {
      setQuantity((parseFloat(notional as string) / parseFloat(price as string)).toFixed(2));
    }
  }, [price, notional]);

  // Update the price when quantity and notional are provided
  useEffect(() => {
    if (quantity && notional) {
      setPrice((parseFloat(notional as string) / parseFloat(quantity as string)).toFixed(2));
    }
  }, [quantity, notional]);

  const handleOrderSubmit = (side: 'BUY' | 'SELL') => {
    const order = {
      asset,
      side,
      type: orderType,
      quantity: typeof quantity === 'string' ? parseFloat(quantity) : quantity,
      price: orderType === 'LIMIT' ? (typeof price === 'string' ? parseFloat(price) : price) : undefined,
      notional: typeof notional === 'string' ? parseFloat(notional) : notional,
    };

    fetch('/trade/', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setMessage(data.error);
        } else {
          setMessage(`Order ${side} successfully placed with id: ${data.id}`);
        }
      })
      .catch(error => setMessage('Order submission failed'));
  };

  return (
    <div className="OrderForm">
      <h2>Order Form</h2>
      <div>
        <label>Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
      </div>
      {orderType === 'LIMIT' && (
        <div>
          <label>Price: </label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
      )}
      <div>
        <label>Notional: </label>
        <input
          type="number"
          value={notional}
          onChange={(e) => setNotional(e.target.value)}
        />
      </div>
      <div>
        <button onClick={() => handleOrderSubmit('BUY')}>Place Buy Order</button>
        <button onClick={() => handleOrderSubmit('SELL')}>Place Sell Order</button>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderForm;
