import React, { useState } from 'react';
import './OrderForm.css';
import { sendTrade } from './api/api';

interface OrderFormProps {
  asset: string;
  addOrder: (order: any) => void;
}

const OrderForm: React.FC<OrderFormProps> = ({ asset, addOrder }) => {
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [notional, setNotional] = useState<number | string>('');
  const [orderType, setOrderType] = useState<'LIMIT' | 'MARKET'>('LIMIT');
  const [message, setMessage] = useState<string>('');

  const handleQuantityChange = (value: string) => {
    setQuantity(value);
    updateNotional(value, price);
  };

  const handlePriceChange = (value: string) => {
    setPrice(value);
    updateNotional(quantity, value);
  };

  const handleNotionalChange = (value: string) => {
    setNotional(value);
    updatePrice(quantity, value);
  };

  const updateNotional = (newQuantity: string | number, newPrice: string | number) => {
    const updatedNotional = parseFloat(newQuantity as string) * parseFloat(newPrice as string);
    setNotional(updatedNotional.toFixed(2));
  };

  const updatePrice = (newQuantity: string | number, newNotional: string | number) => {
    const updatedPrice = parseFloat(newNotional as string) / parseFloat(newQuantity as string);
    setPrice(updatedPrice.toFixed(2));
  };

  const handleOrderSubmit = async (side: 'BUY' | 'SELL') => {
    try {
      if (!quantity || !price || !notional) {
        throw new Error('Please fill in all required fields: Quantity, Price, and Notional.');
      }

      const order = {
        asset,
        side,
        type: orderType,
        quantity: typeof quantity === 'string' ? parseFloat(quantity) : quantity,
        price: orderType === 'LIMIT' ? (typeof price === 'string' ? parseFloat(price) : price) : undefined,
        notional: typeof notional === 'string' ? parseFloat(notional) : notional,
      };

      const response = await sendTrade(order);
      if (response && response.id) {
        setMessage(`Order ${side} successfully placed with id: ${response.id}`);
        addOrder({ ...order, id: response.id });
      } else {
        setMessage('Failed to place order');
      }
    } catch (error) {
      console.error('Error placing order:', error);
      setMessage('Order submission failed');
    }
  };

  return (
    <div className="OrderForm">
      <h2>Order Form</h2>
      <div>
        <label>Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => handleQuantityChange(e.target.value)}
        />
      </div>
      {orderType === 'LIMIT' && (
        <div>
          <label>Price: </label>
          <input
            type="number"
            value={price}
            onChange={(e) => handlePriceChange(e.target.value)}
          />
        </div>
      )}
      <div>
        <label>Notional: </label>
        <input
          type="number"
          value={notional}
          onChange={(e) => handleNotionalChange(e.target.value)}
        />
      </div>
      <div>
        <button onClick={() => handleOrderSubmit('BUY')}>Place Buy Order</button>
        <button onClick={() => handleOrderSubmit('SELL')}>Place Sell Order</button>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderForm;
