import React, { useState } from 'react';
import './OrderForm.css';

interface OrderFormProps {
  asset: string;
  onOrderSubmit: (order: any) => void;
}

const OrderForm: React.FC<OrderFormProps> = ({ asset, onOrderSubmit }) => {
  const [quantity, setQuantity] = useState<number | string>('');
  const [price, setPrice] = useState<number | string>('');
  const [notional, setNotional] = useState<number | string>('');
  const [orderType, setOrderType] = useState<'LIMIT' | 'MARKET'>('LIMIT');
  const [message, setMessage] = useState<string>('');

  const handleOrderSubmit = (side: 'BUY' | 'SELL') => {
    const order = {
      asset,
      side,
      type: orderType,
      quantity: typeof quantity === 'string' ? parseFloat(quantity) : quantity,
      price: orderType === 'LIMIT' ? (typeof price === 'string' ? parseFloat(price) : price) : undefined,
      notional: typeof notional === 'string' ? parseFloat(notional) : notional,
    };

    fetch('http://localhost:3001/trade', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(order),
    })
      .then(response => response.json())
      .then(data => {
        if (data.error) {
          setMessage(data.error);
        } else {
          setMessage(`Order ${side} successfully placed with id: ${data.id}`);
          onOrderSubmit(order); // Call the callback function to update the order book
        }
      })
      .catch(error => setMessage('Order submission failed'));
  };

  return (
    <div className="OrderForm">
      <h2>Place Order</h2>
      <div>
        <label>Quantity: </label>
        <input
          type="number"
          value={quantity}
          onChange={(e) => setQuantity(e.target.value)}
        />
      </div>
      {orderType === 'LIMIT' && (
        <div>
          <label>Price: </label>
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
        </div>
      )}
      <div>
        <label>Notional: </label>
        <input
          type="number"
          value={notional}
          onChange={(e) => setNotional(e.target.value)}
        />
      </div>
      <div>
        <button onClick={() => handleOrderSubmit('BUY')}>Place Buy Order</button>
        <button onClick={() => handleOrderSubmit('SELL')}>Place Sell Order</button>
      </div>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderForm;
